import React, { Component } from "react";

export class Messageboard extends Component {
  constructor() {
    super();
    this.state = {
      installments: 12
    };
  }

  componentDidMount() {
    fetch("/installments")
      .then(res => res.json())
      .then(installment => this.setState({ installments: installment.installments }));
  }

  render() {
    return (
      <div>
        {installments.map(installment => 
        <p key={installments.id}>Monthly installements : {installment.installments}</p>
        )}
      </div>
    );
  }
}

export default Messageboard;
