import React, { Component } from 'react'
import form from 'react-bootstrap';
import axios from 'axios';

export class Dataform extends Component {

    constructor(props) {
        super(props)

        this.state = {
            amount: '',
            duration: ''
          }
    }

    handleChangeAmount = event => {
      this.setState({ amount: event.target.value });
    };

    handleChangeDuration = event => {
      this.setState({ duration: event.target.value });
    };

    handleSubmit = event => {
      event.preventDefault();

      const user = {
        amount: this.state.amount,
        duration: this.state.duration,
      };

      axios.post(`localhost/users`, { user })
        .then(res => {
          console.log(res);
          console.log(res.data);
        });
    };

    render() {
        return (
    <div className="Form-data">
  <form className="form-inline" onSubmit={this.handleSubmit}>
  <div className="form-group mb-2">
    <label htmlFor="inputAmount" className="sr-only">Email</label>
    <input type="number" className="form-control" id="inputAmount" placeholder="Amount" onChange={this.handleChangeAmount} />
  </div>
  <div className="form-group mx-sm-3 mb-2">
    <label htmlFor="inputDuration" className="sr-only">Password</label>
    <input type="number" className="form-control" id="inputDuration" placeholder="Duration" onChange={this.handleChangeDuration}  />
  </div>
  <button type="submit" className="btn btn-primary mb-2">Calculate</button>
</form>
            </div>
        )
    }
}

export default Dataform
