import React from 'react';
import {Form,Row,Col,Button} from 'react-bootstrap/';
import axios from 'axios';

class Formdata extends react.Component() {
state = {
    amount: '',
    duration: '',
  }

  handleChangeAmount = event => {
    this.setState({ amount: event.target.value });
  }

  handleChangeDuration = event => {
    this.setState({ duration: event.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();

    const user = {
      amount: this.state.amount,
      duration: this.state.duration,
    };

    axios.post(`https://jsonplaceholder.typicode.com/users`, { user })
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
  }

  render() {
  return (
    <div className="App">
      <header className="App-header">
        <Form onSubmit={this.handleSubmit}>
          <div className="Form-data">
          <Row>
            <Col sm={5}>
              <input type="text" name="Amount" onChange={this.handleChangeAmount} />
            </Col>
            <Col sm={5}>
              <input type="text" name="Duration" onChange={this.handleChangeDuration} />
            </Col>
            <Col sm={2}>
              <Button type="submit" variant="warning">OK</Button>
            </Col>            
          </Row>
          </div>
        </Form>
          <div className="display-data">
            <p className="display-text">Monthly Installment: <b>5672.63 EUR</b></p>
          </div>
      </header>
    </div>
  );
 }
}

export default Formdata;
